"# scan all json files and generate a new file
output_dir = '/Users/hebi/Library/Application Support/codepod/repos/kicadpcb-converter/output'
for root, dirs, files in os.walk(output_dir):
    for f in files:
        if f.endswith('.json'):
            print('--', f)
            with open(os.path.join(root, f)) as fp:
                jobj = json.load(fp)
            s = board2mat(jobj)
            if s:
                with open(os.path.join(root, f[:-5] + '.list.txt'), 'w') as fp:
                    fp.write(s)
